import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as d,f as p}from"./app.e97fc371.js";const c={},i=p('<h2 id="mysql-5-7-\u548C-mysql-8-\u7684\u533A\u522B" tabindex="-1"><a class="header-anchor" href="#mysql-5-7-\u548C-mysql-8-\u7684\u533A\u522B" aria-hidden="true">#</a> MySql 5.7 \u548C MySql 8 \u7684\u533A\u522B</h2><ol><li><p>NoSql \u5B58\u50A8</p><p>Mysql \u4ECE 5.7 \u7248\u672C\u63D0\u4F9B\u4E86 NoSQL \u7684\u5B58\u50A8\u529F\u80FD,\u5728 8.0 \u4E2D\u8FD9\u90E8\u5206\u5F97\u5230\u4E00\u4E9B\u4FEE\u6539\u3002</p><ul><li><p>\u5173\u7CFB\u578B\u6570\u636E\u5E93 (SQL) \u901A\u8FC7\u5916\u952E\u5173\u8054\u6765\u5EFA\u7ACB\u8868\u4E0E\u8868\u4E4B\u95F4\u7684\u5173\u7CFB</p></li><li><p>\u975E\u5173\u7CFB\u578B\u6570\u636E\u5E93 (NOSQL) \u901A\u5E38\u6307\u6570\u636E\u4EE5\u5BF9\u8C61\u7684\u5F62\u5F0F\u5B58\u50A8\u5728\u6570\u636E\u5E93\u4E2D\uFF0C\u800C\u5BF9\u8C61\u4E4B\u95F4\u7684\u5173\u7CFB\u901A\u8FC7\u6BCF\u4E2A\u5BF9\u8C61\u81EA\u8EAB\u7684\u5C5E\u6027\u6765\u51B3\u5B9A</p></li></ul></li><li><p>\u9690\u85CF\u7D22\u5F15</p><p>\u9690\u85CF\u7D22\u5F15\u7684\u7279\u6027\u5BF9\u4E8E\u6027\u80FD\u8C03\u8BD5\u975E\u5E38\u6709\u7528,\u5728 8.0 \u4E2D,\u7D22\u5F15\u53EF\u4EE5\u88AB\u9690\u85CF\u548C\u663E\u793A,\u5F53\u4E00\u4E2A\u7D22\u5F15\u9690\u85CF\u65F6,\u4ED6\u4E0D\u4F1A\u88AB\u67E5\u8BE2\u4F18\u5316\u5668\u6240\u4F7F\u7528</p><p>\u4E5F\u5C31\u662F\u8BF4\u53EF\u4EE5\u9690\u85CF\u4E00\u4E2A\u7D22\u5F15,\u7136\u540E\u89C2\u5BDF\u5BF9\u6570\u636E\u5E93\u7684\u5F71\u54CD.\u5982\u679C\u6027\u80FD\u4E0B\u964D,\u5C31\u8BF4\u660E\u8FD9\u4E2A\u7D22\u5F15\u662F\u6709\u6548\u7684,\u4E8E\u662F\u5C06\u5176\u201D\u6062\u590D\u663E\u793A\u201D\u5373\u53EF;\u5982\u679C\u6570\u636E\u5E93\u6027\u80FD\u770B\u4E0D\u51FA\u53D8\u5316,\u8BF4\u660E\u8FD9\u4E2A\u7D22\u5F15\u662F\u591A\u4E8E\u7684,\u53EF\u4EE5\u5220\u6389\u4E86</p></li><li><p>\u8BBE\u7F6E\u6301\u4E45\u5316</p><p>MySQL \u7684\u8BBE\u7F6E\u53EF\u4EE5\u5728\u8FD0\u884C\u65F6\u901A\u8FC7 <code>SET GLOBAL</code> \u547D\u4EE4\u6765\u66F4\u6539\uFF0C\u4F46\u662F\u8FD9\u79CD\u66F4\u6539\u53EA\u4F1A\u4E34\u65F6\u751F\u6548\uFF0C\u5230\u4E0B\u6B21\u542F\u52A8\u65F6\u6570\u636E\u5E93\u53C8\u4F1A\u4ECE\u914D\u7F6E\u6587\u4EF6\u4E2D\u8BFB\u53D6\u3002</p><p>MySQL 8.0 \u65B0\u589E\u4E86 <code>SET PERSIST</code> \u547D\u4EE4\uFF0C \u4F8B\u5982: <code>SET PERSIST max_connections = 500;</code> MySQL \u4F1A\u5C06\u8BE5\u547D\u4EE4\u7684\u914D\u7F6E\u4FDD\u5B58\u5230\u6570\u636E\u76EE\u5F55\u4E0B\u7684 <code>mysqld-auto.cnf</code> \u6587\u4EF6\u4E2D\uFF0C\u4E0B\u6B21\u542F\u52A8\u65F6\u4F1A\u8BFB\u53D6\u8BE5\u6587\u4EF6\uFF0C\u7528\u5176\u4E2D\u7684\u914D\u7F6E\u6765\u8986\u76D6\u7F3A\u7701\u7684\u914D\u7F6E\u6587\u4EF6\u3002</p></li><li><p>UTF-8 \u7F16\u7801</p><p>\u4ECE MySQL 8.0 \u5F00\u59CB\uFF0C\u6570\u636E\u5E93\u7684\u7F3A\u7701\u7F16\u7801\u5C06\u6539\u4E3A utf8mb4\uFF0C\u8FD9\u4E2A\u7F16\u7801\u5305\u542B\u4E86\u6240\u6709 emoji \u5B57\u7B26\u3002 \u591A\u5C11\u5E74\u6765\u6211\u4EEC\u4F7F\u7528 MySQL \u90FD\u8981\u5728\u7F16\u7801\u65B9\u9762\u5C0F\u5FC3\u7FFC\u7FFC\uFF0C\u751F\u6015\u5FD8\u4E86\u5C06\u7F3A\u7701\u7684 latin \u6539\u6389\u800C\u51FA\u73B0\u4E71\u7801\u95EE\u9898\u3002\u4ECE\u6B64\u4EE5\u540E\u5C31\u4E0D\u7528\u62C5\u5FC3\u4E86\u3002</p></li><li><p>\u901A\u7528\u8868\u8868\u8FBE\u5F0F (Common Table Expressions)</p><p>\u770B\u4E0A\u53BB\u5C42\u6B21\u548C\u533A\u57DF\u90FD\u66F4\u52A0\u5206\u660E\uFF0C\u6539\u8D77\u6765\u4E5F\u66F4\u6E05\u6670\u7684\u77E5\u9053\u8981\u6539\u54EA\u4E00\u90E8\u5206\u3002</p></li><li><p>\u7A97\u53E3\u51FD\u6570 (Window Functions)</p><p>\u4ECE 8.0 \u5F00\u59CB\uFF0CMySQL \u65B0\u589E\u4E86\u4E00\u4E2A\u53EB\u7A97\u53E3\u51FD\u6570\u7684\u6982\u5FF5\uFF0C\u5B83\u53EF\u4EE5\u7528\u6765\u5B9E\u73B0\u82E5\u5E72\u65B0\u7684\u67E5\u8BE2\u65B9\u5F0F\u3002</p><p>\u7A97\u53E3\u51FD\u6570\u6709\u70B9\u50CF\u662F <code>SUM()</code>\u3001<code>COUNT()</code> \u90A3\u6837\u7684\u96C6\u5408\u51FD\u6570\uFF0C\u4F46\u5B83\u5E76\u4E0D\u4F1A\u5C06\u591A\u884C\u67E5\u8BE2\u7ED3\u679C\u5408\u5E76\u4E3A\u4E00\u884C\uFF0C\u800C\u662F\u5C06\u7ED3\u679C\u653E\u56DE\u591A\u884C\u5F53\u4E2D\u3002\u4E5F\u5C31\u662F\u8BF4\uFF0C\u7A97\u53E3\u51FD\u6570\u662F\u4E0D\u9700\u8981 <code>GROUP BY</code> \u7684\u3002</p></li><li><p>\u65B0\u7684\u7CFB\u7EDF\u5B57\u5178\u8868</p><p>\u6574\u5408\u4E86\u5B58\u50A8\u6709\u5173\u6570\u636E\u5E93\u5BF9\u8C61\u4FE1\u606F\u7684\u4E8B\u52A1\u6570\u636E\u5B57\u5178\uFF0C\u6240\u6709\u7684\u5143\u6570\u636E\u90FD\u7528 <code>InnoDB</code> \u5F15\u64CE\u8FDB\u884C\u5B58\u50A8</p></li><li><p>\u5B89\u5168\u548C\u7528\u6237\u7BA1\u7406</p><p>\u65B0\u589E <code>caching_sha2_password</code> \u8BA4\u8BC1\u63D2\u4EF6\uFF0C\u5E76\u4E14\u662F\u9ED8\u8BA4\u7684\u8EAB\u4EFD\u8BA4\u8BC1\u63D2\u4EF6\u3002\u6027\u80FD\u548C\u5B89\u5168\u65B9\u9762\u52A0\u5F3A</p><p>\u6743\u9650\u652F\u6301 role</p><p>\u65B0\u589E\u5BC6\u7801\u5386\u53F2\u8BB0\u5F55\u529F\u80FD\uFF0C\u9650\u5236\u91CD\u590D\u4F7F\u7528\u4EE5\u524D\u7684\u5BC6\u7801</p></li><li><p>innodb \u589E\u5F3A</p><p>\u65B0\u589E <code>INFORMATION_SCHEMA.INNODB_CACHED_INDEXES</code>\uFF0C\u67E5\u770B\u6BCF\u4E2A\u7D22\u5F15\u7F13\u5B58\u5728 <code>InnoDB</code> \u7F13\u51B2\u6C60\u4E2D\u7684\u7D22\u5F15\u9875\u6570</p><p>\u65B0\u589E <code>INFORMATION_SCHEMA.INNODB_TABLESPACES_BRIEF</code> \u89C6\u56FE</p><p>\u65B0\u589E\u4E86\u52A8\u6001\u914D\u7F6E\u9879 <code>innodb_deadlock_detect</code>\uFF0C\u7528\u6765\u7981\u7528\u6B7B\u9501\u68C0\u67E5\uFF0C\u56E0\u4E3A\u5728\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u4E2D\uFF0C\u5F53\u5927\u91CF\u7EBF\u7A0B\u7B49\u5F85\u540C\u4E00\u4E2A\u9501\u65F6\uFF0C\u6B7B\u9501\u68C0\u67E5\u4F1A\u5927\u5927\u62D6\u6162\u6570\u636E\u5E93</p><p>\u652F\u6301\u4F7F\u7528 <code>innodb_directories</code> \u9009\u9879\u5728\u670D\u52A1\u5668\u8131\u673A\u65F6\u5C06\u8868\u7A7A\u95F4\u6587\u4EF6\u79FB\u52A8\u6216\u6062\u590D\u5230\u65B0\u4F4D\u7F6E</p><p>\u65B0\u589E <code>innodb_dedicated_server</code>\uFF0C\u8BA9 <code>InnoDB</code> \u6839\u636E\u670D\u52A1\u5668\u4E0A\u68C0\u6D4B\u5230\u7684\u5185\u5B58\u91CF\u81EA\u52A8\u914D\u7F6E <code>innodb_buffer_pool_size</code>, <code>innodb_log_file_size\uFF0Cinnodb_flush_method</code>\u3002</p><p><code>innodb_dedicated_server</code>: \u81EA\u52A8\u914D\u7F6E\u7F13\u51B2\u6C60\u5927\u5C0F</p></li><li><p>\u76F4\u65B9\u56FE</p><p>MySQL 8.0 \u7248\u672C\u5F00\u59CB\u652F\u6301\u671F\u5F85\u5DF2\u4E45\u76F4\u65B9\u56FE\u3002\u4F18\u5316\u5668\u4F1A\u5229\u7528 <code>column_statistics</code> \u7684\u6570\u636E\uFF0C\u5224\u65AD\u5B57\u6BB5\u7684\u503C\u7684\u5206\u5E03\uFF0C\u5F97\u5230\u66F4\u51C6\u786E\u7684\u6267\u884C\u8BA1\u5212\u3002</p><p>\u53EF\u4EE5\u4F7F\u7528 <code>ANALYZE TABLE table_name [UPDATE HISTOGRAM on col_name with N BUCKETS |DROP HISTOGRAM ON clo_name]</code> \u6765\u6536\u96C6\u6216\u8005\u5220\u9664\u76F4\u65B9\u56FE\u4FE1\u606F</p><p>\u652F\u6301\u4F1A\u8BDD\u7EA7\u522B <code>SET_VAR</code> \u52A8\u6001\u8C03\u6574\u90E8\u5206\u53C2\u6570\uFF0C\u6709\u5229\u4E8E\u63D0\u5347\u8BED\u53E5\u6027\u80FD\u3002</p></li><li><p>InnoDB \u6027\u80FD\u63D0\u5347</p><p>\u5E9F\u9664 <code>buffer pool mutex</code>, \u5C06\u539F\u6765\u4E00\u4E2A <code>mutex</code> \u62C6\u5206\u6210\u591A\u4E2A\uFF0C\u63D0\u9AD8\u5E76\u53D1\u62C6\u5206 <code>LOCK_thd_list</code> \u548C <code>LOCK_thd_remove</code> \u8FD9\u4E24\u4E2A <code>mutex</code>\uFF0C\u5927\u7EA6\u53EF\u63D0\u9AD8\u7EBF\u7A0B\u94FE\u63A5\u6548\u7387 5%\u3002</p><ol><li><p>\u884C\u7F13\u5B58</p><p>MySQL 8.0 \u7684\u4F18\u5316\u5668\u53EF\u4EE5\u4F30\u7B97\u5C06\u8981\u8BFB\u53D6\u7684\u884C\u6570\uFF0C\u56E0\u6B64\u53EF\u4EE5\u63D0\u4F9B\u7ED9\u5B58\u50A8\u5F15\u64CE\u4E00\u4E2A\u5408\u9002\u5927\u5C0F\u7684 <code>row buffer</code> \u6765\u5B58\u50A8\u9700\u8981\u7684\u6570\u636E\u3002\u5927\u6279\u91CF\u7684\u8FDE\u7EED\u6570\u636E\u626B\u63CF\u7684\u6027\u80FD\u5C06\u53D7\u76CA\u4E8E\u66F4\u5927\u7684 <code>record buffer</code>\u3002</p></li><li><p>\u6539\u8FDB\u626B\u63CF\u6027\u80FD</p><p>\u6539\u8FDB <code>InnoDB</code> \u8303\u56F4\u67E5\u8BE2\u7684\u6027\u80FD\uFF0C\u53EF\u63D0\u5347\u5168\u8868\u67E5\u8BE2\u548C\u8303\u56F4\u67E5\u8BE2 5-20%\u7684\u6027\u80FD\u3002</p></li><li><p>\u6210\u672C\u6A21\u578B</p><p><code>InnoDB</code> \u7F13\u51B2\u533A\u53EF\u4EE5\u4F30\u7B97\u7F13\u5B58\u533A\u4E2D\u7684\u6709\u591A\u5C11\u8868\u548C\u7D22\u5F15\uFF0C\u8FD9\u53EF\u4EE5\u8BA9\u4F18\u5316\u5668\u9009\u62E9\u8BBF\u95EE\u65B9\u5F0F\u65F6\u77E5\u9053\u6570\u636E\u662F\u5426\u53EF\u4EE5\u5B58\u50A8\u5728\u5185\u5B58\u4E2D\u8FD8\u662F\u5FC5\u987B\u5B58\u50A8\u5230\u78C1\u76D8\u4E0A\u3002</p></li></ol></li></ol><h2 id="mysql-5-7-\u548C-8-\u7684\u9009\u62E9" tabindex="-1"><a class="header-anchor" href="#mysql-5-7-\u548C-8-\u7684\u9009\u62E9" aria-hidden="true">#</a> MySql 5.7 \u548C 8 \u7684\u9009\u62E9</h2><p>MySQL 8 \u4F1A\u4F7F\u7528\u66F4\u591A\u5185\u5B58\u3002</p><p>\u5BF9\u8FD0\u884C\u76F8\u540C\u8F7B\u91CF\u7EA7\u5DE5\u4F5C\u8D1F\u8F7D\u7684 MySQL 8 \u548C MySQL 5.7 (\u5B9E\u9645\u4E0A\u5B83\u4EEC\u662F Percona Server \u7248\u672C) \u7684 1GB VM \u8FDB\u884C\u57FA\u672C\u6D4B\u8BD5\u3002</p><p>MySQL 8 \u4F7F\u7528\u4E86\u5927\u7EA6 200MB \u7684 <code>swap</code> \u5206\u533A\uFF0C\u5E76\u4E14\u4F7F\u7528\u66F4\u5C11\u7684\u7CFB\u7EDF\u7F13\u5B58\uFF0C\u88AB\u5206\u914D\u66F4\u591A\u5185\u5B58\u3002</p><p>\u5982\u679C\u6211\u4EEC\u67E5\u770B <code>top</code> \u547D\u4EE4\u7684\u8F93\u51FA\uFF0C\u6211\u4EEC\u4F1A\u770B\u5230 MySQL 8 \u4F7F\u7528\u7684\u66F4\u591A\u5E38\u9A7B\u5185\u5B58\u548C\u865A\u62DF\u5185\u5B58\u3002\u7279\u522B\u662F\u201C\u53EF\u6015\u7684\u201D\u865A\u62DF\u5185\u5B58\uFF0C\u56E0\u4E3A\u5B83\u8FDC\u8FDC\u8D85\u8FC7\u8FD9\u4E9B VM \u4E0A\u53EF\u7528\u7684 1GB \u7269\u7406\u5185\u5B58\u3002\u5F53\u7136\uFF0C\u865A\u62DF\u5185\u5B58\u4F7F\u7528 (VSZ) \u662F\u73B0\u4EE3\u5E94\u7528\u7A0B\u5E8F\u5B9E\u9645\u5185\u5B58\u9700\u6C42\u7684\u4E00\u4E2A\u5F88\u5DEE\u7684\u6307\u6807\uFF0C\u4F46\u5B83\u786E\u5B9E\u8BC1\u5B9E\u4E86\u66F4\u9AD8\u7684\u5185\u5B58\u9700\u6C42\u8FD9\u4E2A\u4E8B\u3002</p><p>\u5B9E\u9645\u4E0A\uFF0C\u6B63\u5982\u6211\u4EEC\u4ECE <code>vmstat</code> \u8F93\u51FA\u4E2D\u6240\u77E5\u9053\u7684\u90A3\u6837\uFF0C\u5373\u4F7F\u6CA1\u6709\u592A\u591A\u7684\u201C\u7A7A\u95F4\u201D\uFF0CMySQL 8 \u548C MySQL 5.7 \u90FD\u4E0D\u4F1A\u5728\u4F4E\u8D1F\u8F7D\u4E0B\u4F7F\u7528 <code>swap</code> \u5206\u533A\u3002</p><p>\u5982\u679C\u60A8\u6709\u591A\u4E2A\u8FDE\u63A5\u6216\u5E0C\u671B\u5728\u540C\u4E00\u4E2A VM \u4E0A\u8FD0\u884C\u67D0\u4E9B\u5E94\u7528\u7A0B\u5E8F\uFF0C\u5219\u53EF\u4EE5\u4F7F\u7528 <code>swap</code> (\u5982\u679C\u672A\u542F\u7528\u4EA4\u6362\uFF0C\u5219\u53EF\u80FD\u5BFC\u81F4 OOM)\u3002\u8FD9\u662F\u4E00\u4E2A\u6709\u8DA3\u7684\u5B9E\u9A8C\uFF0C\u80FD\u770B\u770B\u6211\u6709\u591A\u5C11\u53EF\u4EE5\u9A71\u52A8 MySQL 5.7 \u548C MySQL 8 \u7684\u5185\u5B58\u6D88\u8017\u3002</p><p>\u603B\u7ED3\u5728\u5F00\u53D1\u73AF\u5883\u4E2D\u8FC1\u79FB\u5230 MySQL 8 \u65F6\uFF0C\u8BF7\u8BB0\u4F4F\uFF0C\u4F7F\u7528\u76F8\u540C\u7684\u8BBE\u7F6E\u9700\u8981\u6BD4 MySQL 5.7 \u914D\u7F6E\u66F4\u591A\u7684\u5185\u5B58\u3002</p>',10),l=[i];function n(t,a){return o(),d("div",null,l)}const r=e(c,[["render",n],["__file","compare.html.vue"]]);export{r as default};
